CREATE TABLE IF NOT EXISTS `exam` (`id` INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, `exam_name` varchar(45) DEFAULT NULL);
CREATE TABLE IF NOT EXISTS `exam_year` (`id` INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL,`year` INTEGER(11) NOT NULL,`exam_id` INTEGER(11) NOT NULL);
CREATE TABLE IF NOT EXISTS `questions` (`id` INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL,`subject_id` INTEGER(11) NOT NULL,`question` text);
CREATE TABLE IF NOT EXISTS `question_options` (`id` INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL,`questions_id` INTEGER(11) NOT NULL,`option` text,`isCorrect` INTEGER(11) DEFAULT '0');
CREATE TABLE IF NOT EXISTS `result` (`id` INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL,`user_id` INTEGER(11) NOT NULL,`question_options_id` INTEGER(11) NOT NULL,`questions_id` INTEGER(11) NOT NULL,`isCorrect` INTEGER(11) DEFAULT '0');
CREATE TABLE IF NOT EXISTS `subject` (`id` INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL,`exam_id` INTEGER(11) NOT NULL,`exam_year_id` INTEGER(11) NOT NULL,`name` varchar(45),`time` FLOAT);
CREATE TABLE IF NOT EXISTS `user_info` (`id` INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL,`phonenum` varchar(45) DEFAULT NULL UNIQUE,`email` varchar(45) ,`fullname` varchar(70),country_code INTEGER NOT NULL,activateCode INTEGER, status varchar(1) DEFAULT 'N');
CREATE TABLE IF NOT EXISTS `country` (`id` INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL,`iso` varchar(2),`name` varchar(80),`nicename` varchar(80),`iso3` varchar(3),`numcode` INTEGER(6),`phonecode` INTEGER(5));
